// Process Prompt Evaluation
const evaluationResult = JSON.parse($json.choices[0].message.content);
const responseData = $node["Prompt Exercise Handler"].json.responseData;

// Add evaluation to response
responseData.toolData.evaluationResult = evaluationResult;
responseData.waitingForInput = false;
responseData.nextAction = 'Click Next to continue to the next concept.';

return {
  ...($node["Prompt Exercise Handler"].json),
  evaluationResult,
  responseData
};

// Insert New Concept and Set as Current
const newConcept = JSON.parse($json.choices[0].message.content);

// Get the original data from the correct previous node
const originalData = $node["Generate New Concept Prompt"].json.originalData;
const sessionId = originalData.sessionId;
const orchestratorDecision = originalData.orchestratorDecision;
const sessionState = originalData.sessionState;

// Add metadata to new concept
newConcept.id = `dynamic_${Date.now()}`;
newConcept.isCore = false;
newConcept.isDynamic = true;

// Set targeted gaps from orchestrator decision
const targetGap = orchestratorDecision.conceptNeeded.reason || sessionState.knowledgeGaps[0];
newConcept.targetedGaps = [targetGap];
newConcept.targetedGap = targetGap; // Keep both for compatibility

// Get session from workflow static data
const session = $getWorkflowStaticData('global').sessions[sessionId];

// Increment the attempt counter for this gap
if (targetGap) {
  if (!session.gapAttempts) session.gapAttempts = {};
  session.gapAttempts[targetGap] = (session.gapAttempts[targetGap] || 0) + 1;
  newConcept.attemptNumber = session.gapAttempts[targetGap];
}

// IMPORTANT: Mark the struggling concept as completed so we don't return to it
if (session.currentConcept && session.currentConcept.isCore) {
  // Add the original concept to completed list
  session.completedConcepts.push(session.currentConcept.id);

  // Remove it from remaining core concepts
  session.remainingCoreConcepts = session.remainingCoreConcepts.filter(
    id => id !== session.currentConcept.id
  );
}

// Set the new concept as current
session.currentConcept = newConcept;
session.currentConceptTools = []; // Fresh start for the new concept
session.lastToolUsed = null;

// Clear previous assessment data since this is a new concept
session.lastAssessmentScore = null;
session.knowledgeGaps = []; // Clear the gaps

// Save updated state
$getWorkflowStaticData('global').sessions[sessionId] = session;

// Return with the updated current concept
return {
  ...originalData,
  currentConcept: newConcept,
  sessionState: session, // Pass updated session state
  nextAction: 'concept_card',
  newConceptInserted: true
};

// Complete Current Concept and Move to Next
const sessionId = $json.sessionId;
const currentConcept = $json.currentConcept;
const session = $getWorkflowStaticData('global').sessions[sessionId];

// Mark current concept as completed
if (currentConcept) {
  session.completedConcepts.push(currentConcept.id);

  // Remove from remaining if it was a core concept
  if (currentConcept.isCore) {
    session.remainingCoreConcepts = session.remainingCoreConcepts.filter(
      id => id !== currentConcept.id
    );
  }
}

// Reset for next concept - THIS IS THE FIX
session.currentConcept = null;
session.currentConceptTools = []; // This should be empty!
session.lastToolUsed = null;
session.lastAssessmentScore = null; // Also clear this
session.knowledgeGaps = []; // And this

// Save updated state
$getWorkflowStaticData('global').sessions[sessionId] = session;

// Prepare response to move to next concept
const responseData = {
  sessionId,
  toolType: 'transition',
  conceptProgress: {
    current: session.completedConcepts.length,
    total: session.remainingCoreConcepts.length + 
           session.completedConcepts.length + 
           session.insertedConcepts.length
  },
  toolData: {
    completedConcept: currentConcept?.title,
    message: 'Moving to next concept...'
  },
  waitingForInput: false,
  nextAction: 'auto_continue'
};

return {
  ...($json),
  responseData,
  moveToNext: true
};

// Reset Session
const sessionId = $json.query?.sessionId || 'default-session';

// Clear session data
const sessionState = $getWorkflowStaticData('global');
if (sessionState.sessions && sessionState.sessions[sessionId]) {
  delete sessionState.sessions[sessionId];
}

return {
  success: true,
  message: `Session ${sessionId} has been reset`,
  sessionId
};

// Extract Response Data
const responseData = $json.responseData;

if (!responseData) {
  throw new Error('No responseData found in input');
}

return responseData;

// Prepare Orchestrator OpenAI request
const messages = [
  {
    role: "system",
    content: "You are an adaptive learning orchestrator. Analyze the current learning state and decide the next best action for the learner."
  },
  {
    role: "user", 
    content: `Current Learning State:
- Course Topic: ${$json.courseTopic}
- Current Concept: ${$json.currentConcept?.title || 'None'}
- Tools Already Used: ${$json.sessionState.currentConceptTools.join(', ') || 'None'}
- Last Assessment Score: ${$json.sessionState.lastAssessmentScore || 'N/A'}/5
- Knowledge Gaps: ${$json.sessionState.knowledgeGaps?.join(', ') || 'None'}
- Gap Attempts: ${JSON.stringify($json.sessionState.gapAttempts || {})}
- Recent Questions: ${$json.sessionState.recentQuestions?.join('; ') || 'None'}
- Completed Concepts: ${$json.sessionState.completedConcepts.length}
- Remaining Core Concepts: ${$json.sessionState.remainingCoreConcepts.length}

Rules:
1. Always start new concepts with concept_card
2. Follow concept_card with assessment
3. Only use prompt_exercise if assessment score >= 4 and a prompt task exists
4. If assessment score < 3 or significant gaps exist, use insert_concept INSTEAD of continuing
5. Use concept_complete when all tools for current concept are done

Return JSON:
{
  "nextAction": "concept_card|assessment|prompt_exercise|concept_complete|insert_concept",
  "reasoning": "Brief explanation of decision",
  "conceptNeeded": {
    "reason": "specific gap to address if action is insert_concept",
    "focus": "topic area for new concept if inserting"
  }
}`
  }
];

return {
  messages,
  model: "gpt-4o",
  temperature: 0.3,
  max_tokens: 200,
  response_format: { type: "json_object" },
  // Pass through original data
  originalData: $json
};

// Process Orchestrator Decision - This is where we handle the logic
const orchestratorDecision = JSON.parse($json.choices[0].message.content);
const originalData = $node["Orchestrator Prompt"].json.originalData;
const session = originalData.sessionState;

// Initialize gap tracking if needed
if (!session.gapAttempts) session.gapAttempts = {};
if (!session.deferredGaps) session.deferredGaps = [];

// Check if we're trying to insert a concept for a gap we've already attempted multiple times
if (orchestratorDecision.nextAction === 'insert_concept' && session.knowledgeGaps.length > 0) {
  // Find which gap this would address
  const targetGap = session.knowledgeGaps[0]; // or match from conceptNeeded.reason
  const attempts = session.gapAttempts[targetGap] || 0;

  if (attempts >= 2) {
    // Override the orchestrator's decision
    orchestratorDecision.nextAction = 'concept_complete';
    orchestratorDecision.reasoning = `Gap "${targetGap}" has been attempted ${attempts} times. Moving forward with core curriculum instead of creating another dynamic concept.`;

    // Move gap to deferred list
    session.knowledgeGaps = session.knowledgeGaps.filter(gap => gap !== targetGap);
    session.deferredGaps.push({
      gap: targetGap,
      attempts: attempts,
      timestamp: new Date().toISOString()
    });

    // Clean up attempts counter
    delete session.gapAttempts[targetGap];
  }
}

// Save updated session state
$getWorkflowStaticData('global').sessions[originalData.sessionId] = session;

return {
  ...originalData,
  orchestratorDecision,
  nextAction: orchestratorDecision.nextAction,
  sessionState: session // Pass the updated session state
};

// Combined Grade Assessment and Knowledge Analysis Prompt
const messages = [
  {
    role: "system",
    content: "You are an expert instructional coach who evaluates learner understanding AND tracks their evolving knowledge state. Analyze both the current answer and overall knowledge progression."
  },
  {
    role: "user", 
    content: `Context:
- Course Topic: ${$json.courseTopic}
- Current Concept: ${$json.currentConcept.title}
- Question: ${$json.currentConcept.assessmentQuestion}
- Correct Answer: ${$json.currentConcept.correctAnswer}
- Learner's Answer: ${$json.learnerAnswer}
- Learner Profile: ${$json.learnerProfile.role} at ${$json.learnerProfile.company}

Current Knowledge State:
- Strengths: ${$json.sessionState.knowledgeStrengths?.join(', ') || 'None tracked yet'}
- Gaps: ${$json.sessionState.knowledgeGaps?.join(', ') || 'None tracked yet'}

Assessment Context:
- Is this a dynamic concept?: ${$json.currentConcept.isDynamic || false}
- Targeted gaps (if dynamic): ${$json.currentConcept.targetedGaps?.join(', ') || 'N/A'}
- Previous score: ${$json.sessionState.lastAssessmentScore || 'N/A'}

Evaluate the answer AND update their knowledge state. Return JSON:
{
  "score": (0-5 number),
  "feedback": "Brief, encouraging feedback",
  "understood": (true or false),
  "knowledgeUpdate": {
    "updatedStrengths": ["Complete list of what learner knows well"],
    "updatedGaps": ["Complete list of significant gaps needing attention"],
    "reasoning": "Brief explanation of knowledge state changes"
  }
}

Grading Rules:
- 5: Perfect understanding
- 4: Good understanding with minor gaps
- 3: Adequate understanding for progression
- 2: Significant gaps but some understanding
- 0-1: Major misunderstanding

Knowledge Rules:
- Add strengths for demonstrated understanding (scores 3+)
- Remove gaps that were successfully addressed
- Only list actionable, specific gaps
- For dynamic concepts addressing gaps, be generous if progress is shown
- Empty gaps array means no significant issues`
  }
];

return {
  messages,
  model: "gpt-4o",
  temperature: 0.3,
  max_tokens: 400,
  response_format: { type: "json_object" },
  originalData: $json
};

// Generate Concept Prompt - Prepares prompt for creating new adaptive concepts
const messages = [
  {
    role: "system",
    content: "Generate a new mini-concept to address a specific learning gap. The concept MUST directly target the identified gap, and the assessment question MUST verify the learner understands the gap area. Focus on building a bridge from their current understanding to the correct concept."
  },
  {
    role: "user", 
    content: `Context:
- Main Topic: ${$json.courseTopic}
- Current Concept: ${$json.currentConcept.title}
- Knowledge Gap: ${$json.orchestratorDecision.conceptNeeded.reason}
- Specific Gap to Address: ${$json.sessionState.knowledgeGaps?.[0] || $json.orchestratorDecision.conceptNeeded.focus}
- Learner Profile: ${$json.learnerProfile.role} at ${$json.learnerProfile.company}

The learner scored ${$json.sessionState.lastAssessmentScore}/5 and showed confusion about: "${$json.sessionState.knowledgeGaps?.[0] || 'the core concept'}"

Create a focused concept that DIRECTLY addresses this specific gap: "${$json.orchestratorDecision.conceptNeeded.reason}"

The assessment question MUST test whether the learner understands: ${$json.sessionState.knowledgeGaps?.[0] || $json.orchestratorDecision.conceptNeeded.focus}

Return JSON:
{
  "title": "Clear, specific title",
  "summary": "2-3 sentence overview",
  "expertTips": ["2-3 practical tips"],
  "assessmentQuestion": "Targeted question to verify understanding",
  "correctAnswer": "Brief correct answer",
  "promptTask": "Optional - only if relevant to the gap"
}`
  }
];

return {
  messages,
  model: "gpt-4o",
  temperature: 0.7,
  max_tokens: 400,
  response_format: { type: "json_object" },
  // Pass through original data
  originalData: $json
};
